import React, { useState, useEffect } from 'react';
import axios from 'axios';

const Events = () => {
  const [events, setEvents] = useState([]);
  const [form, setForm] = useState({
    title: '',
    type: 'walk',
    startTime: '',
    endTime: '',
    description: '',
    goal: 0,
  });

  useEffect(() => {
    axios.get('/api/events')
      .then(res => setEvents(res.data))
      .catch(err => console.error(err));
  }, []);

  const handleChange = e => {
    const { name, value } = e.target;
    setForm(prev => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async () => {
    try {
      const res = await axios.post('/api/events', {
        ...form,
        userId: "Dodaj Usera"
      });
      setEvents(prev => [...prev, res.data]);
      setForm({
        title: '',
        type: 'walk',
        startTime: '',
        endTime: '',
        description: '',
        goal: 0
      });
    } catch (err) {
      console.error(err);
    }
  };

  return (
    <div className="event-container">
      <div className="event-form">
        <input name="title" placeholder="Title" value={form.title} onChange={handleChange} />
        <select name="type" value={form.type} onChange={handleChange}>
          <option value="walk">Walk</option>
          <option value="run">Run</option>
          <option value="bike">Bike</option>
        </select>
        <input type="datetime-local" name="startTime" value={form.startTime} onChange={handleChange} />
        <input type="datetime-local" name="endTime" value={form.endTime} onChange={handleChange} />
        <input type="number" name="goal" placeholder="Goal" value={form.goal} onChange={handleChange} />
        <textarea name="description" placeholder="Description" value={form.description} onChange={handleChange} />
        <button onClick={handleSubmit}>＋</button>
      </div>

      <div className="event-list">
        {events.map(e => (
          <div key={e._id} className="event-card">
            <h3>{e.title}</h3>
            <p>{e.type} | Goal: {e.goal}</p>
            <p>{new Date(e.startTime).toLocaleString()} → {e.endTime ? new Date(e.endTime).toLocaleString() : 'Ongoing'}</p>
            <p>{e.description}</p>
          </div>
        ))}
      </div>
    </div>
  );
};

export default Events;
